let subnav = document.getElementById("subnanvar");

window.onscroll = ()=> {runMe()};

function runMe() {
if (document.body.scrollTop > 200 || document.documentElement.scrollTop > 200) {
    subnav.classList.add("addme");
} 
else {
    subnav.classList.remove("addme");
}
}
