import React,{Component} from "react";
import ReactDOM from "react-dom";

class XC extends Component {
  componentDidMount() {
    let sub= document.getElementById("subnanvar")

    window.onscroll = ()=> {runMe()};
    
    function runMe() {
    if (document.body.scrollTop > 200 || document.documentElement.scrollTop > 200) {
        sub.classList.add("addme");
    } 
    else {
        sub.classList.remove("addme");
    }
    }
    ;
  }
  render() {
     
  }
}

export default XC;
